import React, { useState } from "react";
import TripService from "\src\services\TripService.js";
const AddTrip = () => {
    const initialTripState = {
        id: null,
        name: "",
        date: "",
        description: ""
    };

    const [trip, setTrip] = useState(initialTripState);
    const [submitted, setSubmitted] = useState(false);

    const handleInputChange = event => {
        const { name, value } = event.target;
        setTrip({ ...trip, [name]: value });
    };

    const saveTrip = () => {
        var data = {
            name: trip.name,
            date: trip.date,
            description: trip.description
        };

        TripService.create(data)
            .then(res => {
                setTrip({
                    id: res.data.id,
                    name: res.data.name,
                    description: res.data.description
                });

                setSubmitted(true);
                console.log(res.data);
            })
            .catch(e => {
                console.log(e)
            });
    };

    const newTrip = () => {
        setTrip(initialTripState);
        setSubmitted(false);
    };

    return (
        <div className="submit-form">
            {submitted ? (
                <div>
                    <h4>Dodano wycieczkÄ™</h4>
                    <button className="btn btn-success" onClick={newTrip}>Dodaj</button>
                </div>
            ) : (
                <div>
                    <div className="form-group">
                        <label htmlFor="name">Nazwa</label>
                        <input
                            type="text"
                            className="form-control"
                            id="name"
                            required
                            value={trip.name}
                            onChange={handleInputChange}
                            name="name"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="date">Data</label>
                        <input
                            type="text"
                            className="form-control"
                            id="date"
                            required
                            value={trip.date}
                            onChange={handleInputChange}
                            name="date"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="name">Opis</label>
                        <input
                            type="text"
                            className="form-control"
                            id="description"
                            required
                            value={trip.description}
                            onChange={handleInputChange}
                            name="description"
                        />
                    </div>
                    <button onClick={saveTrip} className="btn btn-success">Dodaj</button>
                </div>
            )}
        </div>
    );

};
export default AddTrip;
