import React, { useState, useEffect } from "react";
import AttractionService from "../AttractionService.js";
import Navbar from "../Navbar.js";
import styles from "../Main/styles.module.css"
import { Link } from "react-router-dom";
import TripService from "../TripService.js";

const AddAttraction = () => {
    const initialAttractionState = {
        id: null,
        name: "",
        tripId: ""
    }

    const [attraction, setAttraction] = useState(initialAttractionState);
    const [submitted, setSubmitted] = useState(false);
    const [trips, setTrips] = useState([])

    const saveAttraction = () => {
        var data = {
            name: attraction.name,
            tripId: attraction.tripId,
        };

        AttractionService.create(data)
            .then(res => {
                setAttraction({
                    id: res.data.id,
                    name: res.data.name,
                    tripId: res.data.tripId,
                });

                setSubmitted(true);
                console.log(res.data);
            })
            .catch(e => {
                console.log(e)
            });
    };

    const handleInputChange = event => {
        const { name, value } = event.target;
        setAttraction({ ...attraction, [name]: value });
    };

    const newAttraction = () => {
        setAttraction(initialAttractionState);
        setSubmitted(false)
    }

    useEffect(() => {
        retrieveTrips();
    }, []);

    const retrieveTrips = async () => {
        TripService.getAll()
        .then((res) => {
            console.log(res.data);
            setTrips(res.data);
        })
        .catch((e) => {
            console.log(e);
        });
    }

    return (
        <div>
            <Navbar></Navbar>
            <div className={styles.mainForm}>
                {submitted ? (
                    <div>
                        <h4>Dodano atrakcję</h4>
                        <button className={styles.no_white_btn} onClick={newAttraction}>Dodaj kolejną</button>
                        <Link to="/attractions">
                            <button className={styles.no_white_btn}>Powrót</button>
                        </Link>
                    </div>
                ) : (
                    <div>
                        <h2>Dodaj atrakcję</h2>
                        <div className="form-group">
                            <label htmlFor="name">Nazwa</label>
                            <input
                                type="text"
                                className="form-control"
                                id="name"
                                required
                                value={attraction.name}
                                onChange={handleInputChange}
                                name="name"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="tripId">Wycieczka</label>
                            <select value={attraction.tripId} onChange={handleInputChange}>
                                {trips.map((trip) => (
                                    <option>{trip.name} {trip.date}</option>
                                ))}
                            </select>
                        </div>
                        <div>
                            <br></br>
                            <button onClick={saveAttraction} className={styles.no_white_btn}>Dodaj</button>
                            <Link to="/attractions">
                                <button className={styles.no_white_btn}>Powrót</button>
                            </Link>
                        </div>
                    </div>
                )}
            </div>
        </div>
    )
}
export default AddAttraction
