import React, { useState, useEffect, useRef } from "react";
import AttractionService from "../AttractionService";
import TripService from "../TripService.js";
import { Link } from "react-router-dom";
import styles from "../Main/styles.module.css"
import Navbar from "../Navbar.js";
import axios from "axios";

const AttractionTable = () => {
    const [attractions, setAttraction] = useState([]);
    const [trips, setTrips] = useState([])
    const [trip, setTrip] = useState()
    const attractionRef = useRef();
    attractionRef.current = attractions

    useEffect(() => {
        retrieveAttractions();
        
    }, [])

    useEffect(() => {
        retrieveTrips();
    },[])

    const retrieveAttractions = async () => {
        const token = localStorage.getItem("token")
        if (token) {
            try {
                const config = {
                    method: 'get',
                    url: 'http://localhost:8080/api/attractions',
                    headers: { 'Content-Type': 'application/json', 'x-access-token': token }
                }

                const { data: res } = await axios(config)
                setAttraction(res.data)
            } catch (error) {
                if (error.response && error.response.status >= 400 && error.response.status <= 500) {
                    localStorage.removeItem("token")
                    window.location.reload()
                }
            }
        }
    }

    const refreshTable = () => {
        retrieveAttractions();
        retrieveTrips();
    };

    const removeAllAttractions = () => {
        AttractionService.removeAll()
            .then((res) => {
                console.log(res.data);
                refreshTable();
            })
            .catch((e) => {
                console.log(e);
            });
    };

    const deleteAttraction = (attractionId) => {
        AttractionService.remove(attractionId)
        .then((res) => {
            let newAttractions = [...attractionRef.current]
            newAttractions.splice(attractionId, 1)
            setAttraction(newAttractions)
            window.location.reload()
        })
        .catch((e) => {
            console.log(e)
        })
    }

    const getTrip = (tripId) =>{
        if(trips.length != 0){
            
            trips.forEach((t, i) => {
                console.log(t.id)
                console.log(tripId)
                if(t.id == tripId){
                    console.log(t.name)
                }
            })         
        
        }
            
        // TripService.get(tripId)
        // .then((res) => {
        //     console.log(res.data);
        //     //setTrip(res.data);
        // })
        // .catch((e) => {
        //     console.log(e);
        // });
    }

    const retrieveTrips = async () => {
        const token = localStorage.getItem("token")
        if (token) {
            try {
                const config = {
                    method: 'get',
                    url: 'http://localhost:8080/api/trips',
                    headers: { 'Content-Type': 'application/json', 'x-access-token': token }
                }

                const { data: res } = await axios(config)
                setTrips(res.data)
            } catch (error) {
                if (error.response && error.response.status >= 400 && error.response.status <= 500) {
                    localStorage.removeItem("token")
                    window.location.reload()
                }
            }
        }
    };

    return (
        <div>
            <Navbar></Navbar>
            <div className={styles.main}>
                <div className="list row">
                    <div className="col-md-12 list">
                        <h4>Twoje atrakcje</h4>
                        <Link to="/addattraction">
                            <button className={styles.no_white_btn}>Dodaj</button>
                        </Link>
                        <Link to="/">
                            <button className={styles.no_white_btn}>Powrót</button>
                        </Link>
                        <table className="table table-striped" style={{ marginTop: 20 }}>
                            <thead>
                                <tr>
                                    <th>Nazwa</th>
                                    <th>Wycieczka</th>
                                    <th>Akcja</th>
                                </tr>
                            </thead>
                            <tbody>
                                {attractions.map((attraction) => (
                                    <tr key={attraction.id}>
                                        <td>{attraction.name}</td>
                                        <td>{getTrip(attraction.id)}
                                        </td>
                                        <td>
                                        <Link to={"/attractions/" + attraction.id}>
                                                <span >
                                                    <i className="fas fa-info action mr-2"></i>
                                                </span>
                                            </Link>
                                            <Link to={"/editattraction/" + attraction.id}>
                                                <span >
                                                    <i className="far fa-edit action mr-2"></i>
                                                </span>
                                            </Link>
                                            <span onClick={() => deleteAttraction(attraction.id)}>
                                                <i className="fas fa-trash action"></i>
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    <div className="col-md-12">
                        <Link to="/addattraction">
                            <button className={styles.no_white_btn}>Dodaj</button>
                        </Link>
                        <button className={styles.no_white_btn} onClick={removeAllAttractions}>Usuń wszystko</button>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default AttractionTable
