import styles from "../Main/styles.module.css"
import axios from 'axios';
import React, { useState } from "react";

const Navbar = () => {
    const [setError] = useState([])
    const handleLogout = () => {
        localStorage.removeItem("token")
        window.location.reload()
    }
    const handleRemoveAcc = async (e) => {
        e.preventDefault()
        const token = localStorage.getItem("token")
        if (token) {
            try {
                const config = {
                    method: 'get',
                    url: 'http://localhost:8080/api/users/delete',
                    headers: { 'Content-Type': 'application/json', 'x-access-token': token }
                }
                const { data: res } = await axios(config)
                window.location.reload()
                localStorage.removeItem("token")
                console.log(res.message) 
            } catch (error) {
                if (
                    error.response && error.response.status >= 400 && error.response.status <= 500
                ) {
                    setError(error.response.data.message)
                }
            }
        }
    }

    return(
        <nav className={styles.navbar}>
                <h1>Planer podróży</h1>
                <button className={styles.white_btn} onClick={handleLogout}>Wyloguj się</button>
                <button className={styles.white_btn} onClick={handleRemoveAcc}>Usuń konto</button>
        </nav>
    )
}
export default Navbar;
