import React, { useState, useEffect } from "react";
import Navbar from "../Navbar.js";
import styles from "../Main/styles.module.css"
import { Link, useParams } from "react-router-dom";
import AttractionService from "../AttractionService.js";

const EditAttraction = () => {
    const [attraction, setAttraction] = useState([]);
    const [trips, setTrips] = useState([])
    const [submitted, setSubmitted] = useState(false);
    const { id } = useParams()

    useEffect(() => {
        retrieveAttractions(); 
        retrieveTrips();
    }, [])

    const retrieveAttractions = () =>{
        AttractionService.get(id)
        .then((res) => {
            console.log(res.data);
            setAttraction(res.data);
        })
        .catch((e) => {
            console.log(e);
        });
    }

    const handleInputChange = event => {
        const { name, value } = event.target;
        setAttraction({ ...trip, [name]: value });
    };

    const editAttraction = () => {
        var data = {
            name: attraction.name,
            tripId: attraction.tripId,
        }

        AttractionService.update(id, data)
        .then(res => {
            setAttraction({
                id: res.data.id,
                name: res.data.name,
                tripId: res.data.tripId,
            })
            setSubmitted(true);
        }).catch(e => {
            console.log(e)
        });
    }

    const checkSelected = (tripId, tripid) => {
        if (tripId == tripid) {
            return("selected")
        }
        else return("")
    }

    return (
        <div>
            <Navbar></Navbar>
            <div className={styles.mainForm}>
                {submitted ? (
                    <div>
                        <h4>Zedytowano atrakcję</h4>
                        <button className={styles.no_white_btn} onClick={newAttraction}>Dodaj kolejną</button>
                        <Link to="/attractions">
                            <button className={styles.no_white_btn}>Powrót</button>
                        </Link>
                    </div>
                ) : (
                    <div>
                        <h2>Edytuj atrakcję</h2>
                        <div className="form-group">
                            <label htmlFor="name">Nazwa</label>
                            <input
                                type="text"
                                className="form-control"
                                id="name"
                                required
                                value={attraction.name}
                                onChange={handleInputChange}
                                name="name"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="tripId">Wycieczka</label>
                            <select value={attraction.tripId} onChange={handleInputChange} className="form-control">
                                {trips.map((trip) => (
                                    <option value={trip.id} key={trip.id}>{trip.name} {trip.date}</option>
                                ))}
                            </select>
                        </div>
                        <div>
                            <br></br>
                            <button onClick={editAttraction} className={styles.no_white_btn}>Edytuj</button>
                            <Link to="/attractions">
                                <button className={styles.no_white_btn}>Powrót</button>
                            </Link>
                        </div>
                    </div>
                )}
            </div>
        </div>
    )

}
export default EditAttraction
