    //             accessor: "date",
    //         },
    //         {
    //             Header: "Akcja",
    //             accessor: "actions",
    //             Cell: (props) => {
    //                 const rowIndex = props.row.id;
    //                 return (
    //                     <div>
    //                         <span onClick={() => openTrip(rowIndex)}>
