    //                         <span onClick={() => deleteTrip(rowIndex)}>
    //                             <i className="fas fa-trash action"></i>
    //                         </span>
    //                     </div>
    //                 );
    //             },
    //         },
    //     ], []);

    // const {
    //     getTableProps,
    //     getTableBodyProps,
    //     headerGroups,
    //     rows,
    //     prepareRow,
    // } = useTable ({
    //     columns,
    //     data: trips,
    // });

    return (
    <div>
        <Navbar></Navbar>
        <div className={styles.main}>
        <div className="list row">
            <div className="col-md-6 list">
                <h4>Twoje wycieczki</h4>
                <ul className="list-group">
                    {trips && trips.map((trip, index) => (
                        <li className={"list-group-item " + (index === currentIndex ? "active" : "")} 
                        onClick={() => setActiveTrip(trip, index)}
                        key={index}>{trip.name} {trip.date}</li>
                    ))}
                </ul>
            </div>
            <div className="col-md-12">
                <button className={styles.no_white_btn} onClick={removeAllTrips}>Usuń wszystko</button>
                <Link to="/add">
                    <button className={styles.no_white_btn}>Dodaj</button>
                </Link>
            </div>
        </div>
        </div>
    </div>
    );
};
export default TripTable;
