import { Link } from "react-router-dom";
import React, { useState } from "react";
import AttractionService from "../AttractionService"

const Trip = (props) => {
    const trip = props.trip;
    const [attractions, setAttractions] = useState([])
    
    const getAttractionsByTripId = (tripId) => {
        AttractionService.getAttractionsByTripId(tripId)
        .then((res) => {
            setAttractions(res.data)
        })
        .catch((e) => {
            console.log(e);
        });
    }

    return(
        
            <li> <Link to={"/trips/" + trip.id}>{trip.name} {trip.date}  </Link>
                <ul>
                    {attractions.length > 0 ? <Trips trips={trips} /> : <p></p>}
                </ul>
            </li>
       );
}
export default Trip
