import React, { useState, useEffect, useRef } from "react";
import TripService from "../TripService.js";
import { Link, useParams } from "react-router-dom";
import styles from "../Main/styles.module.css"
import Navbar from "../Navbar.js";
import axios from "axios";
import  { Component } from 'react';
/*
export default class TripDetails extends Component{
    constructor(props) {
        super(props);

        //this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            trip_name: '',
 
        }
    }

    componentDidMount() {
        axios.get('http://localhost:8080/trips/'+this.props.match.params.id)
            .then(response => {
                this.setState({
                    trip_name: response.data.name,
                })   
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    render(){
        return (
            <div>
                <input  type="text"
                                className="form-control"
                                value={this.state.trip_name}
                                
                                />
            </div>
        )
    }
}*/

const  TripDetails = (props) =>{
    const [trip, setTrip] = useState([])
    const { id } = useParams();

    useEffect(() => {
        retrieveTrips();
    }, []);

    const retrieveTrips = () =>{
        TripService.get(id)
        .then((res) => {
            console.log(res.data);
            setTrip(res.data);
        })
        .catch((e) => {
            console.log(e);
        });
    }

    return(
        <div>
            <Navbar></Navbar>
            <div>
            <h2>Szczegóły wycieczki</h2>
            <h4>{trip.name}</h4>
            <p>Data: {trip.date} <br></br> Opis: {trip.description} <br></br>
            </p>
            <Link to="/">
                <button className={styles.no_white_btn}>Powrót</button>
            </Link>
            </div>
        </div>
    )
}



export default TripDetails
