import React, { useState, useEffect } from "react";
import AttractionService from "../AttractionService";
import { Link, useParams } from "react-router-dom";
import styles from "../Main/styles.module.css"
import Navbar from "../Navbar.js";
import TripService from "../TripService.js";

const AttractionDetails = () => {
    const [attraction, setAttraction] = useState([]);
    const [trip, setTrip] = useState([])
    const { id } = useParams();

    useEffect(() => {
        retrieveAttractions(); 
    }, [])

    const retrieveAttractions = () =>{
        AttractionService.get(id)
        .then((res) => {
            console.log(res.data);
            setAttraction(res.data);
        })
        .catch((e) => {
            console.log(e);
        });
    }


    const retrieveTrips = (tripId) =>{
        TripService.get(tripId)
        .then((res) => {
            console.log(res.data);
            setTrip(res.data);
            return (res.data.name)
        })
        .catch((e) => {
            console.log(e);
        });

        
    }

    return(
        <div>
            <Navbar></Navbar>
            <div className={styles.mainForm}>
            <h2>Szczegóły wycieczki</h2>
            <h4>{attraction.name}</h4>
            <p>Wycieczka: {retrieveTrips(attraction.tripId)}</p>
            <Link to="/">
                <button className={styles.no_white_btn}>Powrót</button>
            </Link>
            </div>
        </div>
    )

}
export default AttractionDetails
