import React, { useState, useEffect, useRef, useMemo } from "react";
import TripService from "../TripService.js";
import { useTable } from "react-table";
import { Link } from "react-router-dom";
import styles from "../Main/styles.module.css"

const TripTable = (props) => {
    const [trips, setTrips] = useState([]);
    const tripRef = useRef();
    tripRef.current = trips;

    useEffect(() => {
        retrieveTrips();
    }, []);

    const handleLogout = () => {
        localStorage.removeItem("token")
        window.location.reload()
    }

    const retrieveTrips = () => {
        TripService.getAll()
            .then((res) => {
                setTrips(res.data);
            })
            .catch((e) => {
                console.log(e);
            });
    };

    const refreshTable = () => {
        retrieveTrips();
    };

    const removeAllTrips = () => {
        TripService.removeAll()
            .then((res) => {
                console.log(res.data);
                refreshTable();
            })
            .catch((e) => {
                console.log(e);
            });
    };

    const openTrip = (rowIndex) => {
        const id = tripRef.current[rowIndex].id;
        props.history.push("/trips/" + id);
    };

    const deleteTrip = (rowIndex) => {
        const id = tripRef.current[rowIndex].id;
        TripService.remove(id)
            .then((res) => {
                props.history.push("/trips");
                let newTrips = [...tripRef.current]
                newTrips.splice(rowIndex, 1)
                setTrips(newTrips)
            })
            .catch((e) => {
                console.log(e)
            })
    }

    const columns = useMemo(() =>
        [
            {
                Header: "Nazwa",
                accessor: "name",
            },
            {
                Header: "Data",
                accessor: "date",
            },
            {
                Header: "Akcja",
                accessor: "actions",
                Cell: (props) => {
                    const rowIndex = props.row.id;
                    return (
                        <div>
                            <span onClick={() => openTrip(rowIndex)}>
                                <i className="far fa-edit action mr-2"></i>
                            </span>
                            <span onClick={() => deleteTrip(rowIndex)}>
                                <i className="fas fa-trash action"></i>
                            </span>
                        </div>
                    )
                }
            }
        ], []);

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = useTable({
        columns,
        data: trips,
    });

    return (
    <div>
        <nav className={styles.navbar}>
                <h1>Planer podróży</h1>
                <button className={styles.white_btn} onClick={handleLogout}>
                    Wyloguj się
                </button>
        </nav>
        <div className={styles.main}>
        <div className="list row">
            <div className="col-md-12 list">
                <table className="table table-striped table-bordered" {...getTableProps()}>
                    <thead>
                        {headerGroups.map((headerGroup) => (
                            <tr {...headerGroup.getHeaderGroupProps()}>
                                {headerGroup.headers.map((column) => (
                                    <th {...column.getHeaderProps()}>
                                        {column.render("Header")}
                                    </th>
                                ))}
                            </tr>
                        ))}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                        {rows.map((row, i) => {
                            prepareRow(row);
                            return (
                                <tr {...row.getRowProps()}>
                                    {row.cells.map((cell) => {
                                        return (
                                            <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                                        );
                                    })}
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>
            <div className="col-md-8">
                <button className={styles.no_white_btn} onClick={removeAllTrips}>Usuń wszystko
                </button>
                <Link to="/add">
                    <button className={styles.no_white_btn}>Dodaj</button>
                </Link>
            </div>
        </div>
        </div>
    </div>
    );
};
export default TripTable;
