import React, { useState, useEffect } from "react";
import AttractionService from "../AttractionService";
import { Link, useParams } from "react-router-dom";
import styles from "../Main/styles.module.css"
import Navbar from "../Navbar.js";

const AttractionDetails = () => {
    const [attraction, setAttraction] = useState();
    const [trip, setTrip] = useState()
    const { id } = useParams();

    useEffect(() => {
        retrieveAttractions(); 
        retrieveTrips();
    }, [])

    const retrieveAttractions = () =>{
        AttractionService.get(id)
        .then((res) => {
            console.log(res.data);
            setAttraction(res.data);
        })
        .catch((e) => {
            console.log(e);
        });
    }

    const getTrip = (tripId) =>{
        if(trips.length != 0){
            return(
                trips.find(t => t.id == tripId).name
            )
        }
    }

    const retrieveTrips = () =>{
        TripService.get(attraction.tripId)
        .then((res) => {
            console.log(res.data);
            setTrip(res.data);
        })
        .catch((e) => {
            console.log(e);
        });
    }

    return(
        <div>
            <Navbar></Navbar>
            <div className={styles.mainForm}>
            <h2>Szczegóły wycieczki</h2>
            <h4>{attraction.name}</h4>
            <p>Wycieczka: {getTrip(attraction.tripId)} {trip.name}</p>
            <Link to="/">
                <button className={styles.no_white_btn}>Powrót</button>
            </Link>
            </div>
        </div>
    )

}
export default AttractionDetails
