import React, { useState } from "react";
import Navbar from "../Navbar.js";
import axios from 'axios';
import styles from "../Main/styles.module.css"
import Trips from "./Trips.js";

const TripList = () => {
    const [trips, setTrips, setError] = useState([])

    const handleGetTrips = async (e) => {
        e.preventDefault()
        const token = localStorage.getItem("token")
        if (token) {
            try {
                const config = {
                    method: 'get',
                    url: 'http://localhost:8080/api/trips',
                    headers: { 'Content-Type': 'application/json', 'x-access-token': token }
                }
            
                const { data: res } = await axios(config)
                setTrips(res.data) 
            } catch (error) {
                if (error.response && error.response.status >= 400 && error.response.status <= 500) {
                    setError(error.response.data.message)
                    localStorage.removeItem("token")
                    window.location.reload()
                }
            }
        }
    }

    return(
        <div>
            <Navbar></Navbar>
            <div>
                <button className={styles.no_white_btn} onClick={handleGetTrips}>Lista</button>
                {trips.length > 0 ? <Trips trips={trips} /> : <p>Brak wycieczek</p>}
            </div>
        </div>
    )
}
export default TripList
