import styles from "./styles.module.css"
import axios from 'axios';
import React, { useState } from "react";
import Users from "./Users";
import { Link } from "react-router-dom"
import Navbar from "../Navbar";

const Main = () => {
    const [dane, ustawDane, setError] = useState([])

    const handleGetUsers = async (e) => {
        e.preventDefault()
        const token = localStorage.getItem("token")
        if (token) {
            try {

                const config = {
                    method: 'get',
                    url: 'http://localhost:8080/api/userlist',
                    headers: { 'Content-Type': 'application/json', 'x-access-token': token }
                }
            
                const { data: res } = await axios(config)
                ustawDane(res.data) 
            } catch (error) {
                if (error.response && error.response.status >= 400 && error.response.status <= 500) {
                    setError(error.response.data.message)
                    localStorage.removeItem("token")
                    window.location.reload()
                }
            }
        }
    }
    return (
        <div className={styles.main_container}>
            <Navbar></Navbar>
            {dane.length>0 ? <Users users={dane} /> : <p></p>}
            <div className={styles.main}>
                <h2>Zaplanuj swoją podróż</h2>
                <Link to="/trips">
                    <button className={styles.no_white_btn}>Wycieczki</button>
                </Link>
                <button className={styles.no_white_btn}>Atrakcje</button>
                <Link to="/tripslist">
                    <button className={styles.no_white_btn}>Lista</button>
                </Link>
            </div>
        </div>
    )
}
export default Main
