import React, { useState, useEffect, useRef } from "react";
import TripService from "../TripService.js";
import { Link } from "react-router-dom";
import styles from "../Main/styles.module.css"
import Navbar from "../Navbar.js";
import axios from "axios";

const TripTable = () => {
    const [trips, setTrips] = useState([])
    const tripRef = useRef();
    tripRef.current = trips;

    useEffect(() => {
        retrieveTrips();
    }, []);

    const retrieveTrips = async () => {
        const token = localStorage.getItem("token")
        if (token) {
            try {
                const config = {
                    method: 'get',
                    url: 'http://localhost:8080/api/trips',
                    headers: { 'Content-Type': 'application/json', 'x-access-token': token }
                }

                const { data: res } = await axios(config)
                setTrips(res.data)
            } catch (error) {
                if (error.response && error.response.status >= 400 && error.response.status <= 500) {
                    localStorage.removeItem("token")
                    window.location.reload()
                }
            }
        }
    };

    const refreshTable = () => {
        retrieveTrips();
    };

    const removeAllTrips = () => {
        TripService.removeAll()
            .then((res) => {
                console.log(res.data);
                refreshTable();
            })
            .catch((e) => {
                console.log(e);
            });
    };

    const deleteTrip = (tripId) => {
        TripService.remove(tripId)
            .then((res) => {
                let newTrips = [...tripRef.current]
                newTrips.splice(tripId, 1)
                setTrips(newTrips)
                window.location.reload()
            })
            .catch((e) => {
                console.log(e)
            })
    }

    return (
        <div>
            <Navbar></Navbar>
            <div className={styles.main}>
                <div className="list row">
                    <div className="col-md-12 list">
                        <h4>Twoje wycieczki</h4>
                        <Link to="/addtrip">
                            <button className={styles.no_white_btn}>Dodaj</button>
                        </Link>
                        <Link to="/">
                            <button className={styles.no_white_btn}>Powrót</button>
                        </Link>
                        <table className="table table-striped" style={{ marginTop: 20 }}>
                            <thead>
                                <tr>
                                    <th>Nazwa</th>
                                    <th>Data</th>
                                    <th>Akcja</th>
                                </tr>
                            </thead>
                            <tbody>
                                {trips.map((trip) => (
                                    <tr>
                                        <td>{trip.name}</td>
                                        <td>{trip.date}</td>
                                        <td>
                                            <Link to={"/trips/" + trip.id}>
                                                <span >
                                                    <i className="fas fa-info action mr-2"></i>
                                                </span>
                                            </Link>
                                            <Link to={"/edittrip/" + trip.id}>
                                                <span >
                                                    <i className="far fa-edit action mr-2"></i>
                                                </span>
                                            </Link>
                                            <span onClick={() => deleteTrip(trip.id)}>
                                                <i className="fa-solid fa-trash action"></i>
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    <div className="col-md-12">
                        <Link to="/addtrip">
                            <button className={styles.no_white_btn}>Dodaj</button>
                        </Link>
                        <button className={styles.no_white_btn} onClick={removeAllTrips}>Usuń wszystko</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
export default TripTable
