        //         setTrips(res.data);
        //         console.log(res.data)
        //     })
        //     .catch((e) => {
        //         console.log(e);
        //     });
        const token = localStorage.getItem("token")
        if (token) {
            try {
                const config = {
                    method: 'get',
                    url: 'http://localhost:8080/api/trips',
                    headers: { 'Content-Type': 'application/json', 'x-access-token': token }
                }

                const { data: res } = await axios(config)
                setTrips(res.data)
            } catch (error) {
                if (error.response && error.response.status >= 400 && error.response.status <= 500) {
                    //setError(error.response.data.message)
                    localStorage.removeItem("token")
                    window.location.reload()
                }
            }
        }
    };

    const refreshTable = () => {
        retrieveTrips();
        setCurrentIndex(-1)
        setCurrentTrip(null)
    };

    const setActiveTrip = (trip, index) => {
        setCurrentTrip(trip)
        setCurrentIndex(index)
    }

    const removeAllTrips = () => {
        TripService.removeAll()
            .then((res) => {
                console.log(res.data);
                refreshTable();
            })
            .catch((e) => {
                console.log(e);
            });
    };

    const openTrip = (rowIndex) => {
        const id = tripRef.current[rowIndex].id;
        props.history.push("/trips/" + id);
    };

    const deleteTrip = (rowIndex) => {
        //const id = tripRef.current[rowIndex].id;
        TripService.remove(rowIndex)
            .then((res) => {
                let newTrips = [...tripRef.current]
                newTrips.splice(rowIndex, 1)
                setTrips(newTrips)
                window.location.reload()
            })
            .catch((e) => {
                console.log(e)
            })
    }

    // const columns = useMemo(() =>
