        <div>
            <Navbar></Navbar>
            <div className={styles.main}>
                <div className="list row">
                    <div className="col-md-12 list">
                        <h4>Twoje wycieczki</h4>
                        <ul className="list-group">
                            {trips.map((trip, index) => (
                                <li className={"list-group-item " + (index === currentIndex ? "active" : "")}
                                    onClick={() => setActiveTrip(trip, index)}
                                    key={index}>{trip.name} {trip.date} </li>
                            ))}
                        </ul>
                        <table className="table table-striped" style={{ marginTop: 20 }}>
                            <thead>
                                <tr>
                                    <th>Nazwa</th>
                                    <th>Data</th>
                                    <th>Akcja</th>
                                </tr>
                            </thead>
                            <tbody>
                                {trips.map((trip, index) => (
                                    <tr>
                                        <td>{trip.name}</td>
                                        <td>{trip.date}</td>
                                        <td>
                                        <Link to={"/edit/" + trip.id}>
                                            <span >
                                                <i className="far fa-edit action mr-2"></i>
                                            </span></Link>
                                            <span onClick={() => deleteTrip(trip.id)}>
                                                <i className="fas fa-trash action"></i>
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    <div className="col-md-12">
                        <button className={styles.no_white_btn} onClick={removeAllTrips}>Usu≈Ñ wszystko</button>
                        <Link to="/add">
                            <button className={styles.no_white_btn}>Dodaj</button>
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );

    // constructor(props) {
    //     super(props);
    //     this.state = { trips: [] };
    // }

    // componentDidMount() {
    //     const token = localStorage.getItem("token")
    //     if (token) {
